import numpy as np

from gymnasium import utils
from gymnasium.envs.mujoco import MujocoEnv
from gymnasium.spaces import Box, MultiBinary, Discrete


class TBarEnv(MujocoEnv, utils.EzPickle):

    metadata = {
        "render_modes": [
            "human",
            "rgb_array",
            "depth_array",
        ],
        "render_fps": 250,
    }

    def __init__(self, **kwargs):
        utils.EzPickle.__init__(self, **kwargs)
        #observation_space = Box(low=-np.inf, high=np.inf, shape=(11,), dtype=np.float64)
        observation_space = MultiBinary(8)
        MujocoEnv.__init__(
            self, "/home/kyle/workspaces/TB/Lar_TB/T-bar.xml", 2, observation_space=observation_space, **kwargs
        )

    def step(self, a):
        reward = 1
        self.do_simulation(a, self.frame_skip)
        if self.render_mode == "human":
            self.render()

        ob = self._get_obs()
        return (
            ob,
            reward,
            False,
            False,
            {},
        )

    def viewer_setup(self):
        assert self.viewer is not None
        self.viewer.cam.trackbodyid = 0

    def reset_model(self):
        qpos = self.init_qpos
        qvel = self.init_qvel
        self.set_state(qpos, qvel)
        return self._get_obs()

    def _get_obs(self):
        return np.array([0, 0, 0, 0, 0, 0, 0, 0], dtype=int)